package user

import (
	"testing"

	"github.com/hashicorp/terraform-plugin-framework/types"
	"github.com/stretchr/testify/assert"
)

// shouldResetPassword attempts to detect whether password should be reset to auto-generated.
//
// This function CANNOT correctly distinguish between:
//   - "user removed password from config" (should reset)
//   - "password was never in config" (should NOT reset)
//
// Both cases have: config.Password.IsNull() == true, plan.Password == state.Password.
func shouldResetPassword(plan, state, config *tfModel) bool {
	// Option 1: Compare plan to state
	// Fails because Terraform copies state to plan for Computed+Optional when config is null
	if !plan.Password.Equal(state.Password) {
		return true
	}

	// Option 2: Check if config is null but state has value
	// Detects BOTH "removed from config" AND "never in config" — too broad
	if config.Password.IsNull() && !state.Password.IsNull() {
		return true
	}

	return false
}

func TestShouldResetPassword_CannotDistinguishRemovalFromNeverSet(t *testing.T) {
	// Case 1: Password changed in config → correctly detected
	t.Run("changed_in_config", func(t *testing.T) {
		plan := &tfModel{Password: types.StringValue("NewPass")}
		state := &tfModel{Password: types.StringValue("OldPass")}
		config := &tfModel{Password: types.StringValue("NewPass")}

		assert.True(t, shouldResetPassword(plan, state, config))
	})

	// Case 2: Password removed from config → should reset → detected (correct)
	t.Run("removed_from_config", func(t *testing.T) {
		plan := &tfModel{Password: types.StringValue("CustomPass")}
		state := &tfModel{Password: types.StringValue("CustomPass")}
		config := &tfModel{Password: types.StringNull()}

		assert.True(t, shouldResetPassword(plan, state, config),
			"correctly detects removal, but...")
	})

	// Case 3: Password was never in config → should NOT reset → but detected anyway (BUG)
	t.Run("never_in_config", func(t *testing.T) {
		plan := &tfModel{Password: types.StringValue("AVNautoGenerated")}
		state := &tfModel{Password: types.StringValue("AVNautoGenerated")}
		config := &tfModel{Password: types.StringNull()}

		// BUG: This returns true, but it should return false.
		// There's no way to distinguish this from Case 2 using only plan/state/config.
		assert.True(t, shouldResetPassword(plan, state, config),
			"false positive: would reset auto-generated password on every apply (perpetual diff)")
	})
}
